func:
// Default response if API changes how result is presented.
msg.payload = 'looks like the API has changed ';
msg.payload += 'Please content the flow developers ';
msg.payload += 'and let them know';

// This is where the result was circa 24 Oct 2016
if (msg.result 
        && msg.result.images
        && msg.result.images.length){

    if (!msg.result.images[0].text 
            || 0 == msg.result.images[0].text.length) {
        msg.payload = "I didn't see any text"; 
    } else {
        msg.payload = msg.result.images[0].text;
        // Strip line breaks
        msg.payload = msg.payload.replace(/(\r\n|\n|\r)/gm," ");
    }
}

return msg;
